import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const Dashboard = () => {
    const { user } = useAuth();
    const [loading, setLoading] = useState(true);
    const [stats, setStats] = useState({
        myJournals: 0,
        myEvents: 0,
        myCommunities: 0,
        mySubmissions: 0
    });
    const [recentActivity, setRecentActivity] = useState([]);
    const [upcomingEvents, setUpcomingEvents] = useState([]);

    useEffect(() => {
        fetchDashboardData();
    }, []);

    const fetchDashboardData = async () => {
        try {
            setLoading(true);
            
            // Fetch user statistics
            const [journalsRes, eventsRes, submissionsRes, communitiesRes] = await Promise.all([
                axios.get('/api/journals/count'),
                axios.get('/api/events/count'),
                axios.get('/api/submissions/count'),
                axios.get('/api/communities/count')
            ]);

            setStats({
                myJournals: journalsRes.data.count || 0,
                myEvents: eventsRes.data.count || 0,
                mySubmissions: submissionsRes.data.count || 0,
                myCommunities: communitiesRes.data.count || 0
            });

            // Fetch recent activities
            const activitiesRes = await axios.get('/api/activities/recent');
            setRecentActivity(activitiesRes.data.map(activity => ({
                id: activity.id,
                type: activity.type,
                title: activity.description,
                time: new Date(activity.createdAt).toLocaleDateString(),
                icon: getActivityIcon(activity.type),
                color: getActivityColor(activity.type)
            })));

            // Fetch upcoming events
            const upcomingEventsRes = await axios.get('/api/events/upcoming');
            setUpcomingEvents(upcomingEventsRes.data.map(event => ({
                id: event.id,
                title: event.title,
                date: event.date,
                time: event.time,
                location: event.location,
                status: event.status
            })));

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        } finally {
            setLoading(false);
        }
    };

    const getActivityIcon = (type) => {
        switch (type) {
            case 'journal': return 'fa-book';
            case 'event': return 'fa-calendar-alt';
            case 'community': return 'fa-users';
            case 'submission': return 'fa-upload';
            default: return 'fa-circle';
        }
    };

    const getActivityColor = (type) => {
        switch (type) {
            case 'journal': return 'var(--primary-orange)';
            case 'event': return 'var(--info)';
            case 'community': return 'var(--success)';
            case 'submission': return 'var(--warning)';
            default: return 'var(--text-light)';
        }
    };

    const quickActions = [
        {
            title: 'Create Journal',
            description: 'Start writing your new academic journal',
            icon: 'fa-plus',
            link: '/journals',
            color: 'var(--primary-orange)',
            gradient: 'linear-gradient(135deg, var(--primary-orange), var(--secondary-orange))'
        },
        {
            title: 'Browse Events',
            description: 'Discover upcoming academic events',
            icon: 'fa-search',
            link: '/events',
            color: 'var(--info)',
            gradient: 'linear-gradient(135deg, var(--info), #5DADE2)'
        },
        {
            title: 'Join Community',
            description: 'Connect with like-minded students',
            icon: 'fa-user-plus',
            link: '/communities',
            color: 'var(--success)',
            gradient: 'linear-gradient(135deg, var(--success), #58D68D)'
        },
        {
            title: 'Submit Request',
            description: 'Submit material or facility requests',
            icon: 'fa-paper-plane',
            link: '/submissions',
            color: 'var(--warning)',
            gradient: 'linear-gradient(135deg, var(--warning), #F7DC6F)'
        }
    ];

    if (loading) {
        return (
            <div style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                minHeight: '60vh',
                flexDirection: 'column',
                gap: '1rem'
            }}>
                <div className="spinner"></div>
                <p style={{ color: 'var(--text-light)' }}>Loading your dashboard...</p>
            </div>
        );
    }

    return (
        <div style={{
            minHeight: '100vh',
            background: '#f6f8fa',
            padding: '2rem 0',
        }}>
            <div className="container" style={{ maxWidth: 1200, margin: '0 auto', padding: '0 1rem' }}>
                {/* Welcome Header */}
                <div style={{
                    background: 'linear-gradient(135deg, #ffb347 0%, #ffcc80 100%)',
                    borderRadius: 20,
                    padding: '2.5rem 2rem',
                    marginBottom: '2.5rem',
                    color: '#fff',
                    boxShadow: '0 4px 24px rgba(255,179,71,0.08)',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '2rem',
                }}>
                    <div style={{
                        width: 72,
                        height: 72,
                        borderRadius: '50%',
                        background: 'rgba(255,255,255,0.25)',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        fontSize: 32,
                        fontWeight: 700,
                        boxShadow: '0 2px 8px rgba(0,0,0,0.04)'
                    }}>{user?.name?.charAt(0).toUpperCase() || 'U'}</div>
                    <div>
                        <h1 style={{ margin: 0, fontSize: 32, fontWeight: 800, letterSpacing: -1 }}>Welcome, {user?.name || 'User'}!</h1>
                        <div style={{ display: 'flex', gap: 16, marginTop: 8, flexWrap: 'wrap' }}>
                            <span style={{ fontSize: 15, opacity: 0.9 }}><i className="fas fa-envelope"></i> {user?.email}</span>
                            <span style={{ fontSize: 15, opacity: 0.9 }}><i className="fas fa-user-tag"></i> {user?.role || 'Student'}</span>
                        </div>
                    </div>
                </div>

                {/* Stats Grid */}
                <div style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                    gap: 24,
                    marginBottom: 32,
                }}>
                    <div style={{ background: '#fff', borderRadius: 16, boxShadow: '0 2px 8px rgba(0,0,0,0.04)', padding: 24, textAlign: 'center' }}>
                        <div style={{ fontSize: 28, fontWeight: 700, color: '#ff9800' }}>{stats.myJournals}</div>
                        <div style={{ color: '#888', marginTop: 4 }}><i className="fas fa-book"></i> My Journals</div>
                    </div>
                    <div style={{ background: '#fff', borderRadius: 16, boxShadow: '0 2px 8px rgba(0,0,0,0.04)', padding: 24, textAlign: 'center' }}>
                        <div style={{ fontSize: 28, fontWeight: 700, color: '#2196f3' }}>{stats.myEvents}</div>
                        <div style={{ color: '#888', marginTop: 4 }}><i className="fas fa-calendar-alt"></i> Registered Events</div>
                    </div>
                    <div style={{ background: '#fff', borderRadius: 16, boxShadow: '0 2px 8px rgba(0,0,0,0.04)', padding: 24, textAlign: 'center' }}>
                        <div style={{ fontSize: 28, fontWeight: 700, color: '#43a047' }}>{stats.myCommunities}</div>
                        <div style={{ color: '#888', marginTop: 4 }}><i className="fas fa-users"></i> Joined Communities</div>
                    </div>
                    <div style={{ background: '#fff', borderRadius: 16, boxShadow: '0 2px 8px rgba(0,0,0,0.04)', padding: 24, textAlign: 'center' }}>
                        <div style={{ fontSize: 28, fontWeight: 700, color: '#ffb300' }}>{stats.mySubmissions}</div>
                        <div style={{ color: '#888', marginTop: 4 }}><i className="fas fa-upload"></i> My Submissions</div>
                    </div>
                </div>

                {/* Quick Actions */}
                <div style={{ marginBottom: 40 }}>
                    <h2 style={{ fontSize: 22, fontWeight: 700, color: '#222', marginBottom: 20 }}>Quick Actions</h2>
                    <div style={{
                        display: 'grid',
                        gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))',
                        gap: 20,
                    }}>
                        {quickActions.map((action, idx) => (
                            <Link key={idx} to={action.link} style={{ textDecoration: 'none' }}>
                                <div style={{
                                    background: '#fff',
                                    borderRadius: 16,
                                    boxShadow: '0 2px 8px rgba(0,0,0,0.04)',
                                    padding: '1.5rem',
                                    display: 'flex',
                                    flexDirection: 'column',
                                    alignItems: 'flex-start',
                                    gap: 10,
                                    transition: 'box-shadow 0.2s',
                                    border: `2px solid ${action.color}22`,
                                }}>
                                    <div style={{ fontSize: 24, color: action.color, marginBottom: 6 }}><i className={`fas ${action.icon}`}></i></div>
                                    <div style={{ fontWeight: 600, fontSize: 17, color: '#222' }}>{action.title}</div>
                                    <div style={{ fontSize: 14, color: '#666', opacity: 0.9 }}>{action.description}</div>
                                </div>
                            </Link>
                        ))}
                    </div>
                </div>

                {/* Recent Activity & Upcoming Events */}
                <div style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(auto-fit, minmax(340px, 1fr))',
                    gap: 32,
                }}>
                    {/* Recent Activity */}
                    <div>
                        <h2 style={{ fontSize: 19, fontWeight: 700, color: '#222', marginBottom: 16 }}>Recent Activity</h2>
                        <div style={{ background: '#fff', borderRadius: 16, boxShadow: '0 2px 8px rgba(0,0,0,0.04)', padding: 20, minHeight: 120 }}>
                            {recentActivity.length === 0 ? (
                                <div style={{ color: '#aaa', textAlign: 'center', padding: 24 }}>No recent activity.</div>
                            ) : recentActivity.map((activity) => (
                                <div key={activity.id} style={{ display: 'flex', alignItems: 'center', gap: 14, padding: '10px 0', borderBottom: '1px solid #f0f0f0' }}>
                                    <div style={{ width: 36, height: 36, borderRadius: '50%', background: activity.color, color: '#fff', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 16 }}>
                                        <i className={`fas ${activity.icon}`}></i>
                                    </div>
                                    <div>
                                        <div style={{ fontWeight: 500, color: '#222' }}>{activity.title}</div>
                                        <div style={{ fontSize: 13, color: '#888' }}>{activity.time}</div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                    {/* Upcoming Events */}
                    <div>
                        <h2 style={{ fontSize: 19, fontWeight: 700, color: '#222', marginBottom: 16 }}>Upcoming Events</h2>
                        <div style={{ background: '#fff', borderRadius: 16, boxShadow: '0 2px 8px rgba(0,0,0,0.04)', padding: 20, minHeight: 120 }}>
                            {upcomingEvents.length === 0 ? (
                                <div style={{ color: '#aaa', textAlign: 'center', padding: 24 }}>No upcoming events.</div>
                            ) : upcomingEvents.map((event) => (
                                <div key={event.id} style={{ padding: '10px 0', borderBottom: '1px solid #f0f0f0' }}>
                                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                        <div style={{ fontWeight: 600, color: '#222' }}>{event.title}</div>
                                        <span style={{ fontSize: 13, background: event.status === 'registered' ? '#e8f5e9' : '#e3f2fd', color: event.status === 'registered' ? '#43a047' : '#2196f3', borderRadius: 8, padding: '2px 10px', fontWeight: 500 }}>{event.status === 'registered' ? 'Registered' : 'Available'}</span>
                                    </div>
                                    <div style={{ display: 'flex', gap: 12, fontSize: 13, color: '#888', marginTop: 2 }}>
                                        <div><i className="fas fa-calendar"></i> {new Date(event.date).toLocaleDateString()}</div>
                                        <div><i className="fas fa-clock"></i> {event.time}</div>
                                        <div><i className="fas fa-map-marker-alt"></i> {event.location}</div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
