// frontend/src/pages/Communities.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import authService from '../services/authService';

const Communities = () => {
    const { user } = useAuth();
    const [communities, setCommunities] = useState([]);
    const [myCommunities, setMyCommunities] = useState([]);
    const [loading, setLoading] = useState(true);
    const [filter, setFilter] = useState('all'); // all, joined, available
    const [alert, setAlert] = useState(null);

    useEffect(() => {
        loadCommunities();
    }, []);

    const loadCommunities = async () => {
        try {
            setLoading(true);

            // Load all communities and my communities
            const [allCommunitiesResponse, myCommunitiesResponse] = await Promise.all([
                authService.getCommunities().catch(() => ({ data: [] })),
                authService.getMyCommunities().catch(() => ({ data: [] }))
            ]);

            setCommunities(allCommunitiesResponse.data || []);
            setMyCommunities(myCommunitiesResponse.data || []);
        } catch (error) {
            console.error('Error loading communities:', error);
            // Use database demo data that matches your SQL
            setCommunities([
                {
                    communityId: 1,
                    name: "Technology Enthusiasts",
                    description: "Community for technology lovers and professionals",
                    memberCount: 156,
                    createdAt: "2025-06-09T13:33:49Z",
                    isActive: true,
                    category: "Technology"
                },
                {
                    communityId: 2,
                    name: "Academic Research",
                    description: "Community focused on academic research and publications",
                    memberCount: 89,
                    createdAt: "2025-06-09T13:33:49Z",
                    isActive: true,
                    category: "Research"
                },
                {
                    communityId: 3,
                    name: "Student Organizations",
                    description: "Community for various student organizations",
                    memberCount: 234,
                    createdAt: "2025-06-09T13:33:49Z",
                    isActive: true,
                    category: "Education"
                },
                {
                    communityId: 4,
                    name: "Creative Arts",
                    description: "Community for artists, designers, and creative professionals",
                    memberCount: 78,
                    createdAt: "2025-06-09T13:33:49Z",
                    isActive: true,
                    category: "Arts"
                }
            ]);
        } finally {
            setLoading(false);
        }
    };

    const handleJoinCommunity = async (communityId) => {
        try {
            await authService.joinCommunity(communityId);
            setAlert({ type: 'success', message: 'Successfully joined the community!' });
            loadCommunities(); // Reload to update join status
        } catch (error) {
            setAlert({ type: 'error', message: 'Failed to join community. Please try again.' });
        }
    };

    const handleLeaveCommunity = async (communityId) => {
        try {
            await authService.leaveCommunity(communityId);
            setAlert({ type: 'success', message: 'Successfully left the community!' });
            loadCommunities(); // Reload to update join status
        } catch (error) {
            setAlert({ type: 'error', message: 'Failed to leave community. Please try again.' });
        }
    };

    const handleCreateCommunity = () => {
        // Redirect to create community page or open modal
        setAlert({ type: 'info', message: 'Create community functionality will be implemented soon!' });
    };

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
        });
    };

    const isJoined = (communityId) => {
        return myCommunities.some(community => community.communityId === communityId);
    };

    const getCommunityIcon = (category) => {
        switch (category) {
            case 'Technology': return 'fas fa-laptop-code';
            case 'Research': return 'fas fa-microscope';
            case 'Education': return 'fas fa-graduation-cap';
            case 'Arts': return 'fas fa-palette';
            default: return 'fas fa-users';
        }
    };

    const filteredCommunities = communities.filter(community => {
        switch (filter) {
            case 'joined':
                return isJoined(community.communityId);
            case 'available':
                return !isJoined(community.communityId);
            default:
                return true;
        }
    });

    if (loading) {
        return (
            <div className="communities-page">
                <div className="communities-container">
                    <div className="loading-state">
                        <div className="loading-spinner"></div>
                        <p>Loading communities...</p>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="communities-page">
            <div className="communities-container">
                {/* Header */}
                <div className="communities-header">
                    <div className="header-content">
                        <h1>Communities</h1>
                        <p>Connect with like-minded individuals and join vibrant communities</p>
                    </div>
                    <div className="header-actions">
                        <button className="btn btn-primary" onClick={handleCreateCommunity}>
                            <i className="fas fa-plus"></i>
                            Create Community
                        </button>
                    </div>
                </div>

                {/* Stats */}
                <div className="communities-stats">
                    <div className="stat-item">
                        <span className="stat-number">{communities.length}</span>
                        <span className="stat-label">Total Communities</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-number">{myCommunities.length}</span>
                        <span className="stat-label">Joined</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-number">{communities.reduce((sum, c) => sum + (c.memberCount || 0), 0)}</span>
                        <span className="stat-label">Total Members</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-number">{communities.filter(c => c.isActive).length}</span>
                        <span className="stat-label">Active Communities</span>
                    </div>
                </div>

                {/* Alert */}
                {alert && (
                    <div className={`alert alert-${alert.type}`}>
                        <i className={`fas ${alert.type === 'success' ? 'fa-check-circle' : alert.type === 'error' ? 'fa-exclamation-triangle' : 'fa-info-circle'}`}></i>
                        {alert.message}
                        <button className="alert-close" onClick={() => setAlert(null)}>Ã—</button>
                    </div>
                )}

                {/* Filters */}
                <div className="communities-filters">
                    <div className="filter-tabs">
                        <button
                            className={`filter-tab ${filter === 'all' ? 'active' : ''}`}
                            onClick={() => setFilter('all')}
                        >
                            <i className="fas fa-users"></i>
                            All Communities
                        </button>
                        <button
                            className={`filter-tab ${filter === 'joined' ? 'active' : ''}`}
                            onClick={() => setFilter('joined')}
                        >
                            <i className="fas fa-check-circle"></i>
                            My Communities
                        </button>
                        <button
                            className={`filter-tab ${filter === 'available' ? 'active' : ''}`}
                            onClick={() => setFilter('available')}
                        >
                            <i className="fas fa-search"></i>
                            Discover
                        </button>
                    </div>
                    <div className="search-box">
                        <input
                            type="text"
                            placeholder="Search communities..."
                            className="search-input"
                        />
                        <button className="search-btn">
                            <i className="fas fa-search"></i>
                        </button>
                    </div>
                </div>

                {/* Communities Grid */}
                <div className="communities-grid">
                    {filteredCommunities.length === 0 ? (
                        <div className="empty-state">
                            <div className="empty-icon">
                                <i className="fas fa-users"></i>
                            </div>
                            <h3>No communities found</h3>
                            <p>
                                {filter === 'joined'
                                    ? "You haven't joined any communities yet."
                                    : filter === 'available'
                                        ? "No available communities to join at the moment."
                                        : "No communities available."
                                }
                            </p>
                            {filter !== 'joined' && (
                                <button className="btn btn-primary" onClick={handleCreateCommunity}>
                                    Create New Community
                                </button>
                            )}
                        </div>
                    ) : (
                        filteredCommunities.map(community => {
                            const joined = isJoined(community.communityId);

                            return (
                                <div key={community.communityId} className={`community-card ${joined ? 'joined' : ''}`}>
                                    <div className="community-card-header">
                                        <div className="community-icon">
                                            <i className={getCommunityIcon(community.category)}></i>
                                        </div>
                                        <div className="community-badges">
                                            {joined && (
                                                <span className="badge badge-joined">
                                                    <i className="fas fa-check"></i>
                                                    Joined
                                                </span>
                                            )}
                                            {community.isActive && (
                                                <span className="badge badge-active">
                                                    <i className="fas fa-circle"></i>
                                                    Active
                                                </span>
                                            )}
                                        </div>
                                    </div>

                                    <div className="community-card-content">
                                        <h3 className="community-name">{community.name}</h3>
                                        <p className="community-description">{community.description}</p>
                                        <div className="community-category">
                                            <i className="fas fa-tag"></i>
                                            <span>{community.category}</span>
                                        </div>

                                        <div className="community-stats">
                                            <div className="stat-item">
                                                <i className="fas fa-users"></i>
                                                <span>{community.memberCount || 0} members</span>
                                            </div>
                                            <div className="stat-item">
                                                <i className="fas fa-calendar"></i>
                                                <span>Since {formatDate(community.createdAt)}</span>
                                            </div>
                                        </div>
                                    </div>

                                    <div className="community-card-actions">
                                        <Link
                                            to={`/communities/${community.communityId}`}
                                            className="btn btn-outline btn-sm"
                                        >
                                            <i className="fas fa-eye"></i>
                                            View Details
                                        </Link>

                                        {user && (
                                            <>
                                                {joined ? (
                                                    <button
                                                        className="btn btn-secondary btn-sm"
                                                        onClick={() => handleLeaveCommunity(community.communityId)}
                                                    >
                                                        <i className="fas fa-sign-out-alt"></i>
                                                        Leave
                                                    </button>
                                                ) : (
                                                    <button
                                                        className="btn btn-primary btn-sm"
                                                        onClick={() => handleJoinCommunity(community.communityId)}
                                                    >
                                                        <i className="fas fa-plus"></i>
                                                        Join
                                                    </button>
                                                )}
                                            </>
                                        )}
                                    </div>
                                </div>
                            );
                        })
                    )}
                </div>
            </div>
        </div>
    );
};

export default Communities;
