// frontend/src/pages/Submissions.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { submissionService } from '../services/submission';
import QuickStatusModal from '../components/QuickStatusModal';

const Submissions = () => {
    const navigate = useNavigate();
    const [submissions, setSubmissions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [alert, setAlert] = useState(null);
    const [showForm, setShowForm] = useState(false);
    const [filter, setFilter] = useState('all');
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedSubmission, setSelectedSubmission] = useState(null);
    const [showQuickStatus, setShowQuickStatus] = useState(false);
    const [formData, setFormData] = useState({
        type: 'MATERIAL',
        title: '',
        content: '',
        fileUrl: ''
    });

    const { user } = useAuth();

    useEffect(() => {
        loadSubmissions();
    }, []);

    const loadSubmissions = async () => {
        try {
            setLoading(true);
            setError(null);

            let response;
            // Check if user is admin - if so, get all submissions
            if (user?.roleName === 'ADMIN') {
                response = await submissionService.getAllSubmissions();
            } else {
                response = await submissionService.getUserSubmissions();
            }

            // Ensure we have an array from the response
            const submissionsData = response?.data || [];
            setSubmissions(Array.isArray(submissionsData) ? submissionsData : []);

        } catch (error) {
            console.error('Error loading submissions:', error);
            setError('Failed to load submissions');
            setSubmissions([]); // Set empty array as fallback
        } finally {
            setLoading(false);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await submissionService.createSubmission(formData);
            setAlert({ type: 'success', message: 'Submission created successfully!' });
            setShowForm(false);
            setFormData({
                type: 'MATERIAL',
                title: '',
                content: '',
                fileUrl: ''
            });
            loadSubmissions();
        } catch (error) {
            console.error('Error creating submission:', error);
            setAlert({ type: 'error', message: 'Failed to create submission' });
        }

        // Clear alert after 3 seconds
        setTimeout(() => setAlert(null), 3000);
    };

    const getFilteredSubmissions = () => {
        let filtered = submissions;

        if (filter === 'pending') {
            filtered = submissions.filter(s => s.status === 'PENDING');
        } else if (filter === 'approved') {
            filtered = submissions.filter(s => s.status === 'APPROVED');
        } else if (filter === 'rejected') {
            filtered = submissions.filter(s => s.status === 'REJECTED');
        }

        // Apply search filter
        if (searchTerm) {
            filtered = filtered.filter(submission =>
                submission.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                submission.content?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                submission.type?.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        return filtered;
    };

    const filteredSubmissions = getFilteredSubmissions();

    const getStatusBadge = (status) => {
        const statusClasses = {
            'PENDING': 'badge-warning',
            'APPROVED': 'badge-success',
            'REJECTED': 'badge-danger'
        };
        return statusClasses[status] || 'badge-secondary';
    };

    const getTypeIcon = (type) => {
        return type === 'MATERIAL' ? 'fas fa-file-alt' : 'fas fa-building';
    };

    const formatDate = (dateString) => {
        if (!dateString) return 'Unknown date';
        return new Date(dateString).toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'short',
            day: 'numeric'
        });
    };

    const handleCreateSubmission = () => {
        setShowForm(!showForm);
    };

    const handleQuickStatus = (submission) => {
        setSelectedSubmission(submission);
        setShowQuickStatus(true);
    };

    const handleStatusUpdate = (updatedSubmission) => {
        setSubmissions(prev =>
            prev.map(sub =>
                sub.submissionId === updatedSubmission.submissionId
                    ? updatedSubmission
                    : sub
            )
        );
        setAlert({ type: 'success', message: 'Status updated successfully!' });
        setTimeout(() => setAlert(null), 3000);
    };

    if (loading) {
        return (
            <div className="submissions-loading">
                <div className="loading-content">
                    <div className="loading-spinner"></div>
                    <p>Loading submissions...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="submissions-page">
            <div className="submissions-container">
                {/* Header */}
                <div className="submissions-header">
                    <div className="header-content">
                        <h1>
                            <i className="fas fa-paper-plane"></i>
                            {user?.roleName === 'ADMIN' ? 'All Submissions' : 'My Submissions'}
                        </h1>
                        <p>
                            {user?.roleName === 'ADMIN'
                                ? 'Manage all material and facility submissions'
                                : 'Track your material and facility submissions'
                            }
                        </p>
                    </div>
                    <div className="header-actions">
                        {user?.roleName !== 'ADMIN' && (
                            <button className="btn btn-primary" onClick={handleCreateSubmission}>
                                <i className={`fas ${showForm ? 'fa-times' : 'fa-plus'}`}></i>
                                {showForm ? 'Cancel' : 'New Submission'}
                            </button>
                        )}
                    </div>
                </div>

                {/* Statistics */}
                <div className="submissions-stats">
                    <div className="stat-item">
                        <span className="stat-number">{submissions.length}</span>
                        <span className="stat-label">Total Submissions</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-number">{submissions.filter(s => s.status === 'PENDING').length}</span>
                        <span className="stat-label">Pending</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-number">{submissions.filter(s => s.status === 'APPROVED').length}</span>
                        <span className="stat-label">Approved</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-number">{submissions.filter(s => s.status === 'REJECTED').length}</span>
                        <span className="stat-label">Rejected</span>
                    </div>
                </div>

                {/* Alert */}
                {alert && (
                    <div className={`alert alert-${alert.type === 'error' ? 'danger' : alert.type}`}>
                        <i className={`fas ${alert.type === 'success' ? 'fa-check-circle' : 'fa-exclamation-triangle'}`}></i>
                        {alert.message}
                        <button className="alert-close" onClick={() => setAlert(null)}>×</button>
                    </div>
                )}

                {/* Error */}
                {error && (
                    <div className="alert alert-danger">
                        <i className="fas fa-exclamation-triangle"></i>
                        {error}
                        <button className="alert-close" onClick={() => setError(null)}>×</button>
                    </div>
                )}

                {/* Submission Form */}
                {showForm && user?.roleName !== 'ADMIN' && (
                    <div className="submission-form-card">
                        <div className="card">
                            <div className="card-header">
                                <h5>Create New Submission</h5>
                            </div>
                            <div className="card-body">
                                <form onSubmit={handleSubmit}>
                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="mb-3">
                                                <label htmlFor="type" className="form-label">
                                                    <i className="fas fa-tag"></i> Type
                                                </label>
                                                <select
                                                    className="form-select"
                                                    id="type"
                                                    name="type"
                                                    value={formData.type}
                                                    onChange={handleInputChange}
                                                    required
                                                >
                                                    <option value="MATERIAL">Material Submission</option>
                                                    <option value="FASILITAS">Facility Request</option>
                                                </select>
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="mb-3">
                                                <label htmlFor="title" className="form-label">
                                                    <i className="fas fa-heading"></i> Title
                                                </label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    id="title"
                                                    name="title"
                                                    value={formData.title}
                                                    onChange={handleInputChange}
                                                    placeholder="Enter submission title"
                                                    required
                                                />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="mb-3">
                                        <label htmlFor="content" className="form-label">
                                            <i className="fas fa-align-left"></i> Description
                                        </label>
                                        <textarea
                                            className="form-control"
                                            id="content"
                                            name="content"
                                            rows="4"
                                            value={formData.content}
                                            onChange={handleInputChange}
                                            placeholder="Describe your submission..."
                                            required
                                        ></textarea>
                                    </div>

                                    <div className="mb-3">
                                        <label htmlFor="fileUrl" className="form-label">
                                            <i className="fas fa-link"></i> File URL (Optional)
                                        </label>
                                        <input
                                            type="url"
                                            className="form-control"
                                            id="fileUrl"
                                            name="fileUrl"
                                            value={formData.fileUrl}
                                            onChange={handleInputChange}
                                            placeholder="https://example.com/file.pdf"
                                        />
                                    </div>

                                    <div className="form-actions">
                                        <button type="submit" className="btn btn-primary">
                                            <i className="fas fa-paper-plane"></i>
                                            Submit
                                        </button>
                                        <button
                                            type="button"
                                            className="btn btn-secondary"
                                            onClick={() => setShowForm(false)}
                                        >
                                            <i className="fas fa-times"></i>
                                            Cancel
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                )}

                {/* Filters */}
                <div className="submissions-filters">
                    <div className="filter-tabs">
                        <button
                            className={`filter-tab ${filter === 'all' ? 'active' : ''}`}
                            onClick={() => setFilter('all')}
                        >
                            <i className="fas fa-list"></i>
                            All Submissions
                        </button>
                        <button
                            className={`filter-tab ${filter === 'pending' ? 'active' : ''}`}
                            onClick={() => setFilter('pending')}
                        >
                            <i className="fas fa-clock"></i>
                            Pending
                        </button>
                        <button
                            className={`filter-tab ${filter === 'approved' ? 'active' : ''}`}
                            onClick={() => setFilter('approved')}
                        >
                            <i className="fas fa-check"></i>
                            Approved
                        </button>
                        <button
                            className={`filter-tab ${filter === 'rejected' ? 'active' : ''}`}
                            onClick={() => setFilter('rejected')}
                        >
                            <i className="fas fa-times"></i>
                            Rejected
                        </button>
                    </div>
                    <div className="search-box">
                        <input
                            type="text"
                            placeholder="Search submissions..."
                            className="search-input"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                        <button className="search-btn">
                            <i className="fas fa-search"></i>
                        </button>
                    </div>
                </div>

                {/* Submissions Grid */}
                <div className="submissions-grid">
                    {filteredSubmissions.length === 0 ? (
                        <div className="empty-state">
                            <div className="empty-icon">
                                <i className="fas fa-paper-plane"></i>
                            </div>
                            <h3>
                                {filter !== 'all' ? `No ${filter} submissions` :
                                    user?.roleName === 'ADMIN' ? 'No submissions in system' : 'No submissions yet'}
                            </h3>
                            <p>
                                {filter !== 'all' ? `No submissions with ${filter} status found.` :
                                    user?.roleName === 'ADMIN' ? 'No submissions have been created by users yet.' :
                                        'Create your first submission to get started!'}
                            </p>
                            {user?.roleName !== 'ADMIN' && filter === 'all' && (
                                <button
                                    className="btn btn-primary"
                                    onClick={handleCreateSubmission}
                                >
                                    Create Your First Submission
                                </button>
                            )}
                        </div>
                    ) : (
                        filteredSubmissions.map((submission) => (
                            <div key={submission.submissionId} className="submission-card">
                                <div className="submission-card-header">
                                    <div className="submission-badges">
                                        <span className="badge badge-primary">
                                            <i className={getTypeIcon(submission.type)}></i>
                                            {submission.type}
                                        </span>
                                        <span className={`badge ${getStatusBadge(submission.status)}`}>
                                            {submission.status}
                                        </span>
                                    </div>

                                    <div className="submission-meta">
                                        <span className="submission-date">{formatDate(submission.submittedAt)}</span>
                                    </div>
                                </div>

                                <div className="submission-card-content">
                                    <h3 className="submission-title">{submission.title}</h3>

                                    {/* Show submitter info for admin */}
                                    {user?.roleName === 'ADMIN' && submission.userName && (
                                        <p className="submission-author">By {submission.userName}</p>
                                    )}

                                    <p className="submission-excerpt">
                                        {submission.content?.substring(0, 120)}...
                                    </p>

                                    <div className="submission-stats">
                                        <div className="stat-item">
                                            <i className="fas fa-calendar"></i>
                                            <span>Submitted {formatDate(submission.submittedAt)}</span>
                                        </div>
                                        {submission.fileUrl && (
                                            <div className="stat-item">
                                                <i className="fas fa-paperclip"></i>
                                                <span>File attached</span>
                                            </div>
                                        )}
                                    </div>
                                </div>

                                <div className="submission-card-actions">
                                    <button
                                        className="btn btn-outline btn-sm"
                                        onClick={() => navigate(`/submissions/${submission.submissionId}`)}
                                    >
                                        <i className="fas fa-eye"></i>
                                        View Details
                                    </button>

                                    {submission.fileUrl && (
                                        <a
                                            href={submission.fileUrl}
                                            target="_blank"
                                            rel="noopener noreferrer"
                                            className="btn btn-secondary btn-sm"
                                        >
                                            <i className="fas fa-download"></i>
                                            Download
                                        </a>
                                    )}

                                    {user?.roleName === 'ADMIN' && (
                                        <button
                                            className="btn btn-primary btn-sm"
                                            onClick={() => handleQuickStatus(submission)}
                                        >
                                            <i className="fas fa-edit"></i>
                                            Quick Status
                                        </button>
                                    )}
                                </div>
                            </div>
                        ))
                    )}
                </div>
            </div>

            {/* Quick Status Modal */}
            <QuickStatusModal
                submission={selectedSubmission}
                isOpen={showQuickStatus}
                onClose={() => setShowQuickStatus(false)}
                onStatusUpdate={handleStatusUpdate}
            />
        </div>
    );
};

export default Submissions;
