// frontend/src/pages/UserInfo.jsx
import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

const UserInfo = () => {
    const { user, logout, updateUser } = useAuth();
    const navigate = useNavigate();
    const [isEditing, setIsEditing] = useState(false);
    const [loading, setLoading] = useState(false);
    const [alert, setAlert] = useState(null);
    const [formData, setFormData] = useState({
        name: user?.name || '',
        email: user?.email || '',
        nim: user?.nim || '',
        birthDate: user?.birthDate || '',
        gender: user?.gender || '',
        phone: user?.phone || '',
        province: user?.province || '',
        city: user?.city || ''
    });

    useEffect(() => {
        if (user) {
            setFormData({
                name: user.name || '',
                email: user.email || '',
                nim: user.nim || '',
                birthDate: user.birthDate || '',
                gender: user.gender || '',
                phone: user.phone || '',
                province: user.province || '',
                city: user.city || ''
            });
        }
    }, [user]);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            const response = await api.put('/users/profile', {
                name: formData.name,
                birthDate: formData.birthDate,
                gender: formData.gender,
                phone: formData.phone,
                province: formData.province,
                city: formData.city
            });

            if (response.data) {
                setIsEditing(false);
                setAlert({ type: 'success', message: 'Profile updated successfully!' });
                // Update user context if needed
                if (updateUser) {
                    updateUser(response.data);
                }
            }
        } catch (error) {
            console.error('Error updating profile:', error);
            setAlert({ type: 'error', message: 'Failed to update profile' });
        } finally {
            setLoading(false);
        }

        // Clear alert after 3 seconds
        setTimeout(() => setAlert(null), 3000);
    };

    const handleDelete = async () => {
        if (window.confirm('Are you sure you want to delete your account? This action cannot be undone.')) {
            try {
                await api.delete('/users/profile');
                setAlert({ type: 'success', message: 'Account deleted successfully. Redirecting...' });
                setTimeout(() => {
                    logout();
                    navigate('/');
                }, 2000);
            } catch (error) {
                console.error('Error deleting account:', error);
                setAlert({ type: 'error', message: 'Failed to delete account' });
            }
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return 'Not set';
        return new Date(dateString).toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
        });
    };

    return (
        <div className="profile-page">
            <div className="profile-container">
                {/* Header */}
                <div className="profile-header">
                    <div className="header-content">
                        <h1>
                            <i className="fas fa-user-circle"></i>
                            User Profile
                        </h1>
                        <p>Manage your personal information and account settings</p>
                    </div>
                    <div className="header-actions">
                        <button
                            className="btn btn-secondary me-2"
                            onClick={() => navigate('/dashboard')}
                        >
                            <i className="fas fa-arrow-left"></i>
                            Back to Dashboard
                        </button>
                        {!isEditing ? (
                            <button
                                className="btn btn-primary"
                                onClick={() => setIsEditing(true)}
                            >
                                <i className="fas fa-edit"></i>
                                Edit Profile
                            </button>
                        ) : (
                            <button
                                className="btn btn-outline-secondary"
                                onClick={() => {
                                    setIsEditing(false);
                                    setFormData({
                                        name: user?.name || '',
                                        email: user?.email || '',
                                        nim: user?.nim || '',
                                        birthDate: user?.birthDate || '',
                                        gender: user?.gender || '',
                                        phone: user?.phone || '',
                                        province: user?.province || '',
                                        city: user?.city || ''
                                    });
                                }}
                            >
                                <i className="fas fa-times"></i>
                                Cancel
                            </button>
                        )}
                    </div>
                </div>

                {/* Alert */}
                {alert && (
                    <div className={`alert alert-${alert.type === 'error' ? 'danger' : alert.type}`}>
                        <i className={`fas ${alert.type === 'success' ? 'fa-check-circle' : 'fa-exclamation-triangle'}`}></i>
                        {alert.message}
                        <button className="alert-close" onClick={() => setAlert(null)}></button>
                    </div>
                )}

                <div className="row">
                    {/* Profile Card */}
                    <div className="col-lg-4">
                        <div className="profile-card">
                            <div className="profile-card-header">
                                <div className="profile-avatar-large">
                                    {user?.name?.charAt(0)?.toUpperCase() || 'U'}
                                </div>
                                <h3 className="profile-name">{user?.name || 'User'}</h3>
                                <p className="profile-email">{user?.email}</p>
                                <div className="profile-role">
                                    <span className={`badge ${user?.roleName === 'ADMIN' ? 'badge-danger' : 'badge-primary'}`}>
                                        <i className={user?.roleName === 'ADMIN' ? 'fas fa-user-shield' : 'fas fa-user'}></i>
                                        {user?.roleName || 'USER'}
                                    </span>
                                </div>
                            </div>
                            <div className="profile-card-body">
                                <div className="profile-stats">
                                    <div className="stat-item">
                                        <i className="fas fa-calendar-plus"></i>
                                        <div>
                                            <span className="stat-label">Member Since</span>
                                            <span className="stat-value">{formatDate(user?.createdAt)}</span>
                                        </div>
                                    </div>
                                    <div className="stat-item">
                                        <i className="fas fa-clock"></i>
                                        <div>
                                            <span className="stat-label">Last Updated</span>
                                            <span className="stat-value">{formatDate(user?.updatedAt)}</span>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>

                        {/* Danger Zone */}
                        <div className="danger-zone">
                            <div className="card border-danger">
                                <div className="card-header bg-danger text-white">
                                    <h6 className="mb-0">
                                        <i className="fas fa-exclamation-triangle me-2"></i>
                                        Danger Zone
                                    </h6>
                                </div>
                                <div className="card-body">
                                    <p className="text-muted small mb-3">
                                        Once you delete your account, there is no going back. Please be certain.
                                    </p>
                                    <button
                                        className="btn btn-danger btn-sm w-100"
                                        onClick={handleDelete}
                                    >
                                        <i className="fas fa-trash me-1"></i>
                                        Delete Account
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Profile Information */}
                    <div className="col-lg-8">
                        <div className="profile-info-card">
                            <div className="card">
                                <div className="card-header">
                                    <h5 className="card-title mb-0">
                                        <i className="fas fa-info-circle me-2"></i>
                                        Personal Information
                                    </h5>
                                </div>
                                <div className="card-body">
                                    {isEditing ? (
                                        <form onSubmit={handleUpdate}>
                                            <div className="row">
                                                <div className="col-md-6">
                                                    <div className="mb-3">
                                                        <label className="form-label">
                                                            <i className="fas fa-user me-1"></i>
                                                            Full Name
                                                        </label>
                                                        <input
                                                            type="text"
                                                            className="form-control"
                                                            name="name"
                                                            value={formData.name}
                                                            onChange={handleChange}
                                                            required
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="mb-3">
                                                        <label className="form-label">
                                                            <i className="fas fa-envelope me-1"></i>
                                                            Email (Read Only)
                                                        </label>
                                                        <input
                                                            type="email"
                                                            className="form-control"
                                                            name="email"
                                                            value={formData.email}
                                                            disabled
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="mb-3">
                                                        <label className="form-label">
                                                            <i className="fas fa-id-card me-1"></i>
                                                            NIM
                                                        </label>
                                                        <input
                                                            type="text"
                                                            className="form-control"
                                                            name="nim"
                                                            value={formData.nim}
                                                            onChange={handleChange}
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="mb-3">
                                                        <label className="form-label">
                                                            <i className="fas fa-birthday-cake me-1"></i>
                                                            Birth Date
                                                        </label>
                                                        <input
                                                            type="date"
                                                            className="form-control"
                                                            name="birthDate"
                                                            value={formData.birthDate}
                                                            onChange={handleChange}
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="mb-3">
                                                        <label className="form-label">
                                                            <i className="fas fa-venus-mars me-1"></i>
                                                            Gender
                                                        </label>
                                                        <select
                                                            className="form-select"
                                                            name="gender"
                                                            value={formData.gender}
                                                            onChange={handleChange}
                                                        >
                                                            <option value="">Select Gender</option>
                                                            <option value="LAKI_LAKI">Male</option>
                                                            <option value="PEREMPUAN">Female</option>
                                                        </select>
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="mb-3">
                                                        <label className="form-label">
                                                            <i className="fas fa-phone me-1"></i>
                                                            Phone Number
                                                        </label>
                                                        <input
                                                            type="tel"
                                                            className="form-control"
                                                            name="phone"
                                                            value={formData.phone}
                                                            onChange={handleChange}
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="mb-3">
                                                        <label className="form-label">
                                                            <i className="fas fa-map-marker-alt me-1"></i>
                                                            Province
                                                        </label>
                                                        <input
                                                            type="text"
                                                            className="form-control"
                                                            name="province"
                                                            value={formData.province}
                                                            onChange={handleChange}
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="mb-3">
                                                        <label className="form-label">
                                                            <i className="fas fa-city me-1"></i>
                                                            City
                                                        </label>
                                                        <input
                                                            type="text"
                                                            className="form-control"
                                                            name="city"
                                                            value={formData.city}
                                                            onChange={handleChange}
                                                        />
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="form-actions">
                                                <button
                                                    type="submit"
                                                    className="btn btn-primary"
                                                    disabled={loading}
                                                >
                                                    {loading ? (
                                                        <>
                                                            <span className="spinner-border spinner-border-sm me-2"></span>
                                                            Saving...
                                                        </>
                                                    ) : (
                                                        <>
                                                            <i className="fas fa-save me-1"></i>
                                                            Save Changes
                                                        </>
                                                    )}
                                                </button>
                                            </div>
                                        </form>
                                    ) : (
                                        <div className="profile-display">
                                            <div className="row">
                                                <div className="col-md-6">
                                                    <div className="info-item">
                                                        <label>
                                                            <i className="fas fa-user me-1"></i>
                                                            Full Name
                                                        </label>
                                                        <p>{user?.name || 'Not set'}</p>
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="info-item">
                                                        <label>
                                                            <i className="fas fa-envelope me-1"></i>
                                                            Email
                                                        </label>
                                                        <p>{user?.email}</p>
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="info-item">
                                                        <label>
                                                            <i className="fas fa-id-card me-1"></i>
                                                            NIM
                                                        </label>
                                                        <p>{user?.nim || 'Not set'}</p>
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="info-item">
                                                        <label>
                                                            <i className="fas fa-birthday-cake me-1"></i>
                                                            Birth Date
                                                        </label>
                                                        <p>{formatDate(user?.birthDate)}</p>
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="info-item">
                                                        <label>
                                                            <i className="fas fa-venus-mars me-1"></i>
                                                            Gender
                                                        </label>
                                                        <p>{user?.gender === 'LAKI_LAKI' ? 'Male' : user?.gender === 'PEREMPUAN' ? 'Female' : 'Not set'}</p>
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="info-item">
                                                        <label>
                                                            <i className="fas fa-phone me-1"></i>
                                                            Phone Number
                                                        </label>
                                                        <p>{user?.phone || 'Not set'}</p>
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="info-item">
                                                        <label>
                                                            <i className="fas fa-map-marker-alt me-1"></i>
                                                            Province
                                                        </label>
                                                        <p>{user?.province || 'Not set'}</p>
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="info-item">
                                                        <label>
                                                            <i className="fas fa-city me-1"></i>
                                                            City
                                                        </label>
                                                        <p>{user?.city || 'Not set'}</p>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>

                        {/* Account Security */}
                        <div className="security-card mt-3">
                            <div className="card">
                                <div className="card-header">
                                    <h6 className="card-title mb-0">
                                        <i className="fas fa-shield-alt me-2"></i>
                                        Account Security
                                    </h6>
                                </div>
                                <div className="card-body">
                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="security-item">
                                                <div className="security-icon">
                                                    <i className="fas fa-key"></i>
                                                </div>
                                                <div className="security-content">
                                                    <h6>Password</h6>
                                                    <p className="text-muted small">Last changed: Never</p>
                                                </div>
                                                <button className="btn btn-outline-primary btn-sm">
                                                    Change
                                                </button>
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="security-item">
                                                <div className="security-icon">
                                                    <i className="fas fa-envelope"></i>
                                                </div>
                                                <div className="security-content">
                                                    <h6>Email Verification</h6>
                                                    <p className="text-muted small">Status: Verified</p>
                                                </div>
                                                <span className="badge bg-success">
                                                    <i className="fas fa-check"></i>
                                                    Verified
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default UserInfo;
