// src/components/Navbar.jsx
import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Navbar = () => {
    const { isAuthenticated, user, logout } = useAuth();
    const navigate = useNavigate();
    const location = useLocation();
    const [isScrolled, setIsScrolled] = useState(false);
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

    // Handle scroll effect
    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 50);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    const handleLogout = () => {
        logout();
        navigate('/');
        setIsMobileMenuOpen(false);
    };

    const toggleMobileMenu = () => {
        setIsMobileMenuOpen(!isMobileMenuOpen);
    };

    const closeMobileMenu = () => {
        setIsMobileMenuOpen(false);
    };

    const isActive = (path) => {
        return location.pathname === path;
    };

    return (
        <nav className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
            <div className="container nav-wrapper">
                {/* Brand */}
                <Link to="/" className="navbar-brand">
                    <div className="brand-logo">I</div>
                    <span className="brand-text">IYIP Platform</span>
                </Link>

                {/* Desktop Navigation */}
                <div className="nav-menu d-none d-md-flex">
                    <Link
                        to="/"
                        className={`nav-link ${isActive('/') ? 'active' : ''}`}
                        onClick={closeMobileMenu}
                    >
                        Home
                    </Link>

                    {isAuthenticated && (
                        <>
                            <Link
                                to="/dashboard"
                                className={`nav-link ${isActive('/dashboard') ? 'active' : ''}`}
                                onClick={closeMobileMenu}
                            >
                                Dashboard
                            </Link>
                            <Link
                                to="/events"
                                className={`nav-link ${isActive('/events') ? 'active' : ''}`}
                                onClick={closeMobileMenu}
                            >
                                Events
                            </Link>
                            <Link
                                to="/journals"
                                className={`nav-link ${isActive('/journals') ? 'active' : ''}`}
                                onClick={closeMobileMenu}
                            >
                                Journals
                            </Link>
                            <Link
                                to="/communities"
                                className={`nav-link ${isActive('/communities') ? 'active' : ''}`}
                                onClick={closeMobileMenu}
                            >
                                Communities
                            </Link>
                            <Link
                                to="/submissions"
                                className={`nav-link ${isActive('/submissions') ? 'active' : ''}`}
                                onClick={closeMobileMenu}
                            >
                                Submissions
                            </Link>
                        </>
                    )}
                </div>

                {/* Desktop Auth Section */}
                <div className="nav-auth d-none d-md-flex">
                    {isAuthenticated ? (
                        <div className="user-menu">
                            {/* FIXED: User info with proper width */}
                            <Link
                                to="/profile"
                                className="user-info"
                                style={{ textDecoration: 'none' }}
                            >
                                <div className="user-avatar">
                                    {user?.name?.charAt(0).toUpperCase() || 'U'}
                                </div>
                                <span className="user-name">
                                    {user?.email || 'user@iyip.com'} USER
                                </span>
                            </Link>
                            <button
                                className="btn btn-sm btn-outline"
                                onClick={handleLogout}
                            >
                                Logout
                            </button>
                        </div>
                    ) : (
                        <>
                            <Link to="/login" className="btn btn-sm btn-outline">
                                Login
                            </Link>
                            <Link to="/register" className="btn btn-sm btn-primary">
                                Register
                            </Link>
                        </>
                    )}
                </div>

                {/* Mobile Menu Toggle */}
                <button
                    className={`mobile-menu-toggle d-md-none ${isMobileMenuOpen ? 'active' : ''}`}
                    onClick={toggleMobileMenu}
                    aria-label="Toggle mobile menu"
                >
                    <div className="hamburger"></div>
                </button>
            </div>

            {/* Mobile Navigation Menu */}
            {isMobileMenuOpen && (
                <div className={`mobile-nav-menu d-md-none ${isMobileMenuOpen ? 'open' : ''}`}>
                    <Link
                        to="/"
                        className={`mobile-nav-link ${isActive('/') ? 'active' : ''}`}
                        onClick={closeMobileMenu}
                    >
                        Home
                    </Link>

                    {isAuthenticated ? (
                        <>
                            <Link
                                to="/dashboard"
                                className={`mobile-nav-link ${isActive('/dashboard') ? 'active' : ''}`}
                                onClick={closeMobileMenu}
                            >
                                Dashboard
                            </Link>
                            <Link
                                to="/events"
                                className={`mobile-nav-link ${isActive('/events') ? 'active' : ''}`}
                                onClick={closeMobileMenu}
                            >
                                Events
                            </Link>
                            <Link
                                to="/journals"
                                className={`mobile-nav-link ${isActive('/journals') ? 'active' : ''}`}
                                onClick={closeMobileMenu}
                            >
                                Journals
                            </Link>
                            <Link
                                to="/communities"
                                className={`mobile-nav-link ${isActive('/communities') ? 'active' : ''}`}
                                onClick={closeMobileMenu}
                            >
                                Communities
                            </Link>
                            <Link
                                to="/submissions"
                                className={`mobile-nav-link ${isActive('/submissions') ? 'active' : ''}`}
                                onClick={closeMobileMenu}
                            >
                                Submissions
                            </Link>

                            <div className="mobile-auth-buttons">
                                <Link
                                    to="/profile"
                                    className="mobile-user-info"
                                    onClick={closeMobileMenu}
                                    style={{ textDecoration: 'none', color: 'inherit' }}
                                >
                                    <div className="user-avatar">
                                        {user?.name?.charAt(0).toUpperCase() || 'U'}
                                    </div>
                                    <span>{user?.email || 'user@iyip.com'} USER</span>
                                </Link>
                                <button
                                    className="btn btn-outline w-100"
                                    onClick={handleLogout}
                                >
                                    Logout
                                </button>
                            </div>
                        </>
                    ) : (
                        <div className="mobile-auth-buttons">
                            <Link
                                to="/login"
                                className="btn btn-outline w-100"
                                onClick={closeMobileMenu}
                            >
                                Login
                            </Link>
                            <Link
                                to="/register"
                                className="btn btn-primary w-100"
                                onClick={closeMobileMenu}
                            >
                                Register
                            </Link>
                        </div>
                    )}
                </div>
            )}
        </nav>
    );
};

export default Navbar;
