// src/pages/Dashboard.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const Dashboard = () => {
    const { user } = useAuth();
    const [loading, setLoading] = useState(true);
    const [stats, setStats] = useState({
        myJournals: 0,
        myEvents: 0,
        myCommunities: 0,
        mySubmissions: 0
    });
    const [recentActivity, setRecentActivity] = useState([]);
    const [upcomingEvents, setUpcomingEvents] = useState([]);

    useEffect(() => {
        fetchDashboardData();
    }, []);

    const fetchDashboardData = async () => {
        try {
            setLoading(true);
            
            // Fetch user statistics
            const [journalsRes, eventsRes, submissionsRes, communitiesRes] = await Promise.all([
                axios.get('/api/journals/count'),
                axios.get('/api/events/count'),
                axios.get('/api/submissions/count'),
                axios.get('/api/communities/count')
            ]);

            setStats({
                myJournals: journalsRes.data.count || 0,
                myEvents: eventsRes.data.count || 0,
                mySubmissions: submissionsRes.data.count || 0,
                myCommunities: communitiesRes.data.count || 0
            });

            // Fetch recent activities
            const activitiesRes = await axios.get('/api/activities/recent');
            setRecentActivity(activitiesRes.data.map(activity => ({
                id: activity.id,
                type: activity.type,
                title: activity.description,
                time: new Date(activity.createdAt).toLocaleDateString(),
                icon: getActivityIcon(activity.type),
                color: getActivityColor(activity.type)
            })));

            // Fetch upcoming events
            const upcomingEventsRes = await axios.get('/api/events/upcoming');
            setUpcomingEvents(upcomingEventsRes.data.map(event => ({
                id: event.id,
                title: event.title,
                date: event.date,
                time: event.time,
                location: event.location,
                status: event.status
            })));

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        } finally {
            setLoading(false);
        }
    };

    const getActivityIcon = (type) => {
        switch (type) {
            case 'journal': return 'fa-book';
            case 'event': return 'fa-calendar-alt';
            case 'community': return 'fa-users';
            case 'submission': return 'fa-upload';
            default: return 'fa-circle';
        }
    };

    const getActivityColor = (type) => {
        switch (type) {
            case 'journal': return 'var(--primary-orange)';
            case 'event': return 'var(--info)';
            case 'community': return 'var(--success)';
            case 'submission': return 'var(--warning)';
            default: return 'var(--text-light)';
        }
    };

    const quickActions = [
        {
            title: 'Create Journal',
            description: 'Start writing your new academic journal',
            icon: 'fa-plus',
            link: '/journals',
            color: 'var(--primary-orange)',
            gradient: 'linear-gradient(135deg, var(--primary-orange), var(--secondary-orange))'
        },
        {
            title: 'Browse Events',
            description: 'Discover upcoming academic events',
            icon: 'fa-search',
            link: '/events',
            color: 'var(--info)',
            gradient: 'linear-gradient(135deg, var(--info), #5DADE2)'
        },
        {
            title: 'Join Community',
            description: 'Connect with like-minded students',
            icon: 'fa-user-plus',
            link: '/communities',
            color: 'var(--success)',
            gradient: 'linear-gradient(135deg, var(--success), #58D68D)'
        },
        {
            title: 'Submit Request',
            description: 'Submit material or facility requests',
            icon: 'fa-paper-plane',
            link: '/submissions',
            color: 'var(--warning)',
            gradient: 'linear-gradient(135deg, var(--warning), #F7DC6F)'
        }
    ];

    if (loading) {
        return (
            <div style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                minHeight: '60vh',
                flexDirection: 'column',
                gap: '1rem'
            }}>
                <div className="spinner"></div>
                <p style={{ color: 'var(--text-light)' }}>Loading your dashboard...</p>
            </div>
        );
    }

    return (
        <div style={{ 
            minHeight: '100vh', 
            background: 'var(--light-gray)',
            padding: '2rem 0'
        }}>
            <div className="container">
                {/* Welcome Header */}
                <div style={{
                    background: 'var(--gradient-primary)',
                    borderRadius: 'var(--border-radius-lg)',
                    padding: '3rem',
                    marginBottom: '3rem',
                    color: 'var(--white)',
                    position: 'relative',
                    overflow: 'hidden'
                }}>
                    {/* Background decoration */}
                    <div style={{
                        position: 'absolute',
                        top: '-50px',
                        right: '-50px',
                        width: '200px',
                        height: '200px',
                        background: 'rgba(255, 255, 255, 0.1)',
                        borderRadius: '50%'
                    }}></div>
                    <div style={{
                        position: 'absolute',
                        bottom: '-30px',
                        left: '-30px',
                        width: '150px',
                        height: '150px',
                        background: 'rgba(255, 255, 255, 0.08)',
                        borderRadius: '50%'
                    }}></div>

                    <div style={{ position: 'relative', zIndex: 1 }}>
                        <div style={{
                            display: 'flex',
                            alignItems: 'center',
                            gap: '1.5rem',
                            marginBottom: '1rem'
                        }}>
                            <div style={{
                                width: '80px',
                                height: '80px',
                                background: 'rgba(255, 255, 255, 0.2)',
                                borderRadius: '50%',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                fontSize: '2rem',
                                fontWeight: 'bold'
                            }}>
                                {user?.name?.charAt(0).toUpperCase() || 'U'}
                            </div>
                            <div>
                                <h1 style={{ 
                                    margin: '0',
                                    fontSize: '2.5rem',
                                    fontWeight: '800'
                                }}>
                                    Welcome back, {user?.name || 'User'}! 👋
                                </h1>
                                <p style={{ 
                                    margin: '0.5rem 0 0',
                                    fontSize: '1.2rem',
                                    opacity: '0.9'
                                }}>
                                    Ready to continue your academic journey?
                                </p>
                            </div>
                        </div>
                        
                        <div style={{
                            display: 'flex',
                            gap: '1rem',
                            alignItems: 'center',
                            flexWrap: 'wrap'
                        }}>
                            <div style={{
                                background: 'rgba(255, 255, 255, 0.2)',
                                padding: '0.5rem 1rem',
                                borderRadius: 'var(--border-radius)',
                                fontSize: '0.9rem'
                            }}>
                                <i className="fas fa-envelope"></i>
                                {user?.email}
                            </div>
                            <div style={{
                                background: 'rgba(255, 255, 255, 0.2)',
                                padding: '0.5rem 1rem',
                                borderRadius: 'var(--border-radius)',
                                fontSize: '0.9rem'
                            }}>
                                <i className="fas fa-user-tag"></i>
                                {user?.role || 'Student'}
                            </div>
                            <div style={{
                                background: 'rgba(255, 255, 255, 0.2)',
                                padding: '0.5rem 1rem',
                                borderRadius: 'var(--border-radius)',
                                fontSize: '0.9rem'
                            }}>
                                <i className="fas fa-clock"></i>
                                Last login: Today
                            </div>
                        </div>
                    </div>
                </div>

                {/* Stats Grid */}
                <div className="stats-grid" style={{ marginBottom: '3rem' }}>
                    <div className="stat-card">
                        <div className="stat-number">{stats.myJournals}</div>
                        <div className="stat-label">
                            <i className="fas fa-book"></i>
                            My Journals
                        </div>
                    </div>
                    <div className="stat-card">
                        <div className="stat-number">{stats.myEvents}</div>
                        <div className="stat-label">
                            <i className="fas fa-calendar-alt"></i>
                            Registered Events
                        </div>
                    </div>
                    <div className="stat-card">
                        <div className="stat-number">{stats.myCommunities}</div>
                        <div className="stat-label">
                            <i className="fas fa-users"></i>
                            Joined Communities
                        </div>
                    </div>
                    <div className="stat-card">
                        <div className="stat-number">{stats.mySubmissions}</div>
                        <div className="stat-label">
                            <i className="fas fa-upload"></i>
                            My Submissions
                        </div>
                    </div>
                </div>

                {/* Quick Actions */}
                <div style={{ marginBottom: '3rem' }}>
                    <h2 style={{
                        fontSize: '2rem',
                        fontWeight: '700',
                        color: 'var(--text-dark)',
                        marginBottom: '1.5rem'
                    }}>
                        Quick Actions
                    </h2>
                    <div style={{
                        display: 'grid',
                        gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
                        gap: '1.5rem'
                    }}>
                        {quickActions.map((action, index) => (
                            <Link 
                                key={index}
                                to={action.link}
                                style={{
                                    textDecoration: 'none',
                                    color: 'inherit'
                                }}
                            >
                                <div style={{
                                    background: action.gradient,
                                    padding: '2rem',
                                    borderRadius: 'var(--border-radius-lg)',
                                    color: 'var(--white)',
                                    transition: 'transform 0.2s ease',
                                    cursor: 'pointer',
                                    '&:hover': {
                                        transform: 'translateY(-5px)'
                                    }
                                }}>
                                    <div style={{
                                        fontSize: '2rem',
                                        marginBottom: '1rem'
                                    }}>
                                        <i className={`fas ${action.icon}`}></i>
                                    </div>
                                    <h3 style={{
                                        margin: '0 0 0.5rem',
                                        fontSize: '1.5rem',
                                        fontWeight: '600'
                                    }}>
                                        {action.title}
                                    </h3>
                                    <p style={{
                                        margin: '0',
                                        opacity: '0.9',
                                        fontSize: '0.9rem'
                                    }}>
                                        {action.description}
                                    </p>
                                </div>
                            </Link>
                        ))}
                    </div>
                </div>

                {/* Recent Activity and Upcoming Events */}
                <div style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                    gap: '2rem'
                }}>
                    {/* Recent Activity */}
                    <div>
                        <h2 style={{
                            fontSize: '1.5rem',
                            fontWeight: '600',
                            color: 'var(--text-dark)',
                            marginBottom: '1.5rem'
                        }}>
                            Recent Activity
                        </h2>
                        <div style={{
                            background: 'var(--white)',
                            borderRadius: 'var(--border-radius-lg)',
                            padding: '1.5rem',
                            boxShadow: 'var(--shadow-sm)'
                        }}>
                            {recentActivity.map((activity) => (
                                <div 
                                    key={activity.id}
                                    style={{
                                        display: 'flex',
                                        alignItems: 'flex-start',
                                        gap: '1rem',
                                        padding: '1rem 0',
                                        borderBottom: '1px solid var(--border-color)',
                                        '&:last-child': {
                                            borderBottom: 'none',
                                            paddingBottom: '0'
                                        }
                                    }}
                                >
                                    <div style={{
                                        width: '40px',
                                        height: '40px',
                                        borderRadius: '50%',
                                        background: activity.color,
                                        display: 'flex',
                                        alignItems: 'center',
                                        justifyContent: 'center',
                                        color: 'var(--white)',
                                        fontSize: '1.2rem'
                                    }}>
                                        <i className={`fas ${activity.icon}`}></i>
                                    </div>
                                    <div>
                                        <p style={{
                                            margin: '0 0 0.25rem',
                                            color: 'var(--text-dark)',
                                            fontWeight: '500'
                                        }}>
                                            {activity.title}
                                        </p>
                                        <span style={{
                                            fontSize: '0.85rem',
                                            color: 'var(--text-light)'
                                        }}>
                                            {activity.time}
                                        </span>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>

                    {/* Upcoming Events */}
                    <div>
                        <h2 style={{
                            fontSize: '1.5rem',
                            fontWeight: '600',
                            color: 'var(--text-dark)',
                            marginBottom: '1.5rem'
                        }}>
                            Upcoming Events
                        </h2>
                        <div style={{
                            background: 'var(--white)',
                            borderRadius: 'var(--border-radius-lg)',
                            padding: '1.5rem',
                            boxShadow: 'var(--shadow-sm)'
                        }}>
                            {upcomingEvents.map((event) => (
                                <div 
                                    key={event.id}
                                    style={{
                                        padding: '1rem 0',
                                        borderBottom: '1px solid var(--border-color)',
                                        '&:last-child': {
                                            borderBottom: 'none',
                                            paddingBottom: '0'
                                        }
                                    }}
                                >
                                    <div style={{
                                        display: 'flex',
                                        justifyContent: 'space-between',
                                        alignItems: 'flex-start',
                                        marginBottom: '0.5rem'
                                    }}>
                                        <h3 style={{
                                            margin: '0',
                                            fontSize: '1.1rem',
                                            fontWeight: '600',
                                            color: 'var(--text-dark)'
                                        }}>
                                            {event.title}
                                        </h3>
                                        <span style={{
                                            padding: '0.25rem 0.75rem',
                                            borderRadius: 'var(--border-radius)',
                                            fontSize: '0.8rem',
                                            fontWeight: '500',
                                            background: event.status === 'registered' ? 'var(--success-light)' : 'var(--info-light)',
                                            color: event.status === 'registered' ? 'var(--success)' : 'var(--info)'
                                        }}>
                                            {event.status === 'registered' ? 'Registered' : 'Available'}
                                        </span>
                                    </div>
                                    <div style={{
                                        display: 'flex',
                                        gap: '1rem',
                                        fontSize: '0.9rem',
                                        color: 'var(--text-light)'
                                    }}>
                                        <div>
                                            <i className="fas fa-calendar"></i>
                                            {new Date(event.date).toLocaleDateString()}
                                        </div>
                                        <div>
                                            <i className="fas fa-clock"></i>
                                            {event.time}
                                        </div>
                                        <div>
                                            <i className="fas fa-map-marker-alt"></i>
                                            {event.location}
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
