import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserInfo = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        axios.get('/api/auth/me')
            .then(res => {
                setUser(res.data);
                setLoading(false);
            })
            .catch(() => setLoading(false));
    }, []);

    if (loading) return <div>Loading...</div>;
    if (!user) return <div>User info not found.</div>;

    return (
        <div className="user-info-page">
            <h2>Informasi User</h2>
            <ul>
                <li><strong>Nama:</strong> {user.name}</li>
                <li><strong>Email:</strong> {user.email}</li>
                <li><strong>NIM:</strong> {user.nim}</li>
                <li><strong>Role:</strong> {user.roleName}</li>
                <li><strong>Provinsi:</strong> {user.province}</li>
                <li><strong>Kota:</strong> {user.city}</li>
                <li><strong>Tanggal Lahir:</strong> {user.birthDate}</li>
                <li><strong>Jenis Kelamin:</strong> {user.gender}</li>
                <li><strong>No. HP:</strong> {user.phone}</li>
            </ul>
        </div>
    );
};

export default UserInfo;
