// src/components/Navbar.jsx
import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Navbar = () => {
    const { isAuthenticated, user, logout } = useAuth();
    const navigate = useNavigate();
    const location = useLocation();
    const [isScrolled, setIsScrolled] = useState(false);
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

    // Handle scroll effect
    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 50);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    const handleLogout = () => {
        logout();
        navigate('/');
        setIsMobileMenuOpen(false);
    };

    const toggleMobileMenu = () => {
        setIsMobileMenuOpen(!isMobileMenuOpen);
    };

    const closeMobileMenu = () => {
        setIsMobileMenuOpen(false);
    };

    const isActive = (path) => {
        return location.pathname === path;
    };

    return (
        <nav className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
            <div className="container">
                <div style={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    justifyContent: 'space-between',
                    width: '100%'
                }}>
                    {/* Brand */}
                    <Link to="/" className="navbar-brand">
                        <span style={{ 
                            display: 'flex', 
                            alignItems: 'center', 
                            gap: '0.5rem' 
                        }}>
                            <div style={{
                                width: '40px',
                                height: '40px',
                                background: 'var(--gradient-primary)',
                                borderRadius: '12px',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                color: 'white',
                                fontWeight: 'bold',
                                fontSize: '1.2rem'
                            }}>
                                I
                            </div>
                            IYIP Platform
                        </span>
                    </Link>

                    {/* Desktop Navigation */}
                    <div className="navbar-nav d-none d-md-flex">
                        <Link 
                            to="/" 
                            className={`nav-link ${isActive('/') ? 'active' : ''}`}
                        >
                            <i className="fas fa-home"></i>
                            Home
                        </Link>

                        {isAuthenticated ? (
                            <>
                                <Link 
                                    to="/dashboard" 
                                    className={`nav-link ${isActive('/dashboard') ? 'active' : ''}`}
                                >
                                    <i className="fas fa-chart-line"></i>
                                    Dashboard
                                </Link>
                                <Link 
                                    to="/events" 
                                    className={`nav-link ${isActive('/events') ? 'active' : ''}`}
                                >
                                    <i className="fas fa-calendar-alt"></i>
                                    Events
                                </Link>
                                <Link 
                                    to="/journals" 
                                    className={`nav-link ${isActive('/journals') ? 'active' : ''}`}
                                >
                                    <i className="fas fa-book"></i>
                                    Journals
                                </Link>
                                <Link 
                                    to="/communities" 
                                    className={`nav-link ${isActive('/communities') ? 'active' : ''}`}
                                >
                                    <i className="fas fa-users"></i>
                                    Communities
                                </Link>
                                <Link 
                                    to="/submissions" 
                                    className={`nav-link ${isActive('/submissions') ? 'active' : ''}`}
                                >
                                    <i className="fas fa-upload"></i>
                                    Submissions
                                </Link>

                                {/* User Dropdown */}
                                <div className="nav-dropdown">
                                    <button 
                                        className="nav-link user-menu-btn"
                                        onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                                    >
                                        <div style={{
                                            width: '32px',
                                            height: '32px',
                                            background: 'var(--gradient-primary)',
                                            borderRadius: '50%',
                                            display: 'flex',
                                            alignItems: 'center',
                                            justifyContent: 'center',
                                            color: 'white',
                                            fontWeight: 'bold',
                                            marginRight: '0.5rem'
                                        }}>
                                            {user?.name?.charAt(0).toUpperCase() || 'U'}
                                        </div>
                                        {user?.name || 'User'}
                                        <i className="fas fa-chevron-down"></i>
                                    </button>
                                </div>

                                <button 
                                    onClick={handleLogout}
                                    className="btn btn-outline"
                                    style={{ marginLeft: '1rem' }}
                                >
                                    <i className="fas fa-sign-out-alt"></i>
                                    Logout
                                </button>
                            </>
                        ) : (
                            <>
                                <Link to="/login" className="btn btn-outline">
                                    <i className="fas fa-sign-in-alt"></i>
                                    Login
                                </Link>
                                <Link to="/register" className="btn btn-primary">
                                    <i className="fas fa-user-plus"></i>
                                    Register
                                </Link>
                            </>
                        )}
                    </div>

                    {/* Mobile Menu Button */}
                    <button 
                        className="mobile-menu-btn d-md-none"
                        onClick={toggleMobileMenu}
                        style={{
                            background: 'none',
                            border: 'none',
                            fontSize: '1.5rem',
                            color: 'var(--primary-orange)',
                            cursor: 'pointer',
                            padding: '0.5rem',
                            borderRadius: '8px',
                            transition: 'var(--transition)'
                        }}
                    >
                        <i className={`fas ${isMobileMenuOpen ? 'fa-times' : 'fa-bars'}`}></i>
                    </button>
                </div>

                {/* Mobile Navigation */}
                {isMobileMenuOpen && (
                    <div 
                        className="mobile-nav"
                        style={{
                            position: 'absolute',
                            top: '100%',
                            left: '0',
                            right: '0',
                            background: 'rgba(255, 255, 255, 0.98)',
                            backdropFilter: 'blur(20px)',
                            boxShadow: 'var(--shadow-lg)',
                            borderRadius: '0 0 20px 20px',
                            padding: '2rem',
                            animation: 'slideDown 0.3s ease'
                        }}
                    >
                        <div style={{
                            display: 'flex',
                            flexDirection: 'column',
                            gap: '1rem'
                        }}>
                            <Link 
                                to="/" 
                                className={`nav-link ${isActive('/') ? 'active' : ''}`}
                                onClick={closeMobileMenu}
                            >
                                <i className="fas fa-home"></i>
                                Home
                            </Link>

                            {isAuthenticated ? (
                                <>
                                    <Link 
                                        to="/dashboard" 
                                        className={`nav-link ${isActive('/dashboard') ? 'active' : ''}`}
                                        onClick={closeMobileMenu}
                                    >
                                        <i className="fas fa-chart-line"></i>
                                        Dashboard
                                    </Link>
                                    <Link 
                                        to="/events" 
                                        className={`nav-link ${isActive('/events') ? 'active' : ''}`}
                                        onClick={closeMobileMenu}
                                    >
                                        <i className="fas fa-calendar-alt"></i>
                                        Events
                                    </Link>
                                    <Link 
                                        to="/journals" 
                                        className={`nav-link ${isActive('/journals') ? 'active' : ''}`}
                                        onClick={closeMobileMenu}
                                    >
                                        <i className="fas fa-book"></i>
                                        Journals
                                    </Link>
                                    <Link 
                                        to="/communities" 
                                        className={`nav-link ${isActive('/communities') ? 'active' : ''}`}
                                        onClick={closeMobileMenu}
                                    >
                                        <i className="fas fa-users"></i>
                                        Communities
                                    </Link>
                                    <Link 
                                        to="/submissions" 
                                        className={`nav-link ${isActive('/submissions') ? 'active' : ''}`}
                                        onClick={closeMobileMenu}
                                    >
                                        <i className="fas fa-upload"></i>
                                        Submissions
                                    </Link>

                                    <div style={{ 
                                        borderTop: '1px solid var(--border-color)', 
                                        paddingTop: '1rem',
                                        marginTop: '1rem'
                                    }}>
                                        <div style={{
                                            display: 'flex',
                                            alignItems: 'center',
                                            gap: '0.75rem',
                                            padding: '0.75rem',
                                            background: 'var(--light-orange)',
                                            borderRadius: 'var(--border-radius)',
                                            marginBottom: '1rem'
                                        }}>
                                            <div style={{
                                                width: '40px',
                                                height: '40px',
                                                background: 'var(--gradient-primary)',
                                                borderRadius: '50%',
                                                display: 'flex',
                                                alignItems: 'center',
                                                justifyContent: 'center',
                                                color: 'white',
                                                fontWeight: 'bold'
                                            }}>
                                                {user?.name?.charAt(0).toUpperCase() || 'U'}
                                            </div>
                                            <div>
                                                <div style={{ fontWeight: '600', color: 'var(--text-dark)' }}>
                                                    {user?.name || 'User'}
                                                </div>
                                                <div style={{ fontSize: '0.875rem', color: 'var(--text-light)' }}>
                                                    {user?.email}
                                                </div>
                                            </div>
                                        </div>

                                        <button 
                                            onClick={handleLogout}
                                            className="btn btn-danger"
                                            style={{ width: '100%' }}
                                        >
                                            <i className="fas fa-sign-out-alt"></i>
                                            Logout
                                        </button>
                                    </div>
                                </>
                            ) : (
                                <div style={{
                                    display: 'flex',
                                    flexDirection: 'column',
                                    gap: '1rem',
                                    marginTop: '1rem'
                                }}>
                                    <Link 
                                        to="/login" 
                                        className="btn btn-outline"
                                        onClick={closeMobileMenu}
                                        style={{ width: '100%' }}
                                    >
                                        <i className="fas fa-sign-in-alt"></i>
                                        Login
                                    </Link>
                                    <Link 
                                        to="/register" 
                                        className="btn btn-primary"
                                        onClick={closeMobileMenu}
                                        style={{ width: '100%' }}
                                    >
                                        <i className="fas fa-user-plus"></i>
                                        Register
                                    </Link>
                                </div>
                            )}
                        </div>
                    </div>
                )}
            </div>

            <style jsx>{`
                @keyframes slideDown {
                    from {
                        opacity: 0;
                        transform: translateY(-20px);
                    }
                    to {
                        opacity: 1;
                        transform: translateY(0);
                    }
                }

                .mobile-menu-btn:hover {
                    background: var(--light-orange) !important;
                    transform: scale(1.1);
                }

                .user-menu-btn {
                    display: flex !important;
                    align-items: center !important;
                    gap: 0.5rem !important;
                    background: var(--light-orange) !important;
                    border-radius: var(--border-radius) !important;
                    padding: 0.75rem 1rem !important;
                    border: none !important;
                    cursor: pointer !important;
                    transition: var(--transition) !important;
                }

                .user-menu-btn:hover {
                    background: var(--accent-orange) !important;
                    transform: translateY(-2px) !important;
                }

                @media (max-width: 768px) {
                    .mobile-nav {
                        margin: 0 -20px;
                    }
                }
            `}</style>
        </nav>
    );
};

export default Navbar;
